import React, { useState, useRef, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useProfiles } from "../context/ProfileContext";
import { useCertificates } from "../context/CertificateContext";
import { getImageUrl } from '../utils/config';
import { UserCircleIcon, CameraIcon, PencilIcon, AcademicCapIcon, BriefcaseIcon, CalendarIcon, MapPinIcon, PhoneIcon, EnvelopeIcon } from '@heroicons/react/24/outline';

export default function Profile() {
  const { user } = useAuth();
  const { profiles, uploadProfilePicture } = useProfiles();
  const { certificates } = useCertificates();
  const [uploading, setUploading] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');
  const fileInputRef = useRef(null);

  // Find current user's profile or use auth user data
  const userProfile = profiles.find(p => p.email === user?.email) || user || {};

  // Get user's certificates
  const userCertificates = certificates.filter(cert => 
    cert.profileName === `${userProfile?.firstName || user?.firstName} ${userProfile?.lastName || user?.lastName}`
  );

  // Update profile when userProfile changes (especially email)
  const [profile, setProfile] = useState(userProfile);

  useEffect(() => {
    setProfile(userProfile);
  }, [userProfile]);

  // handle profile picture change
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setProfile((prev) => ({ ...prev, photo: imageUrl }));
    }
  };

  return (
    <div className="p-6">
      {/* Header row with title + edit button */}
      <div className="flex items-center justify-between mb-2">
        <h1 className="text-2xl font-bold">My Profile</h1>
        <button
          onClick={() => navigate("/editprofile")}
          className="text-sm border px-4 py-2 rounded bg-green-600 text-white hover:bg-green-700 shadow"
        >
          Edit
        </button>
      </div>

      {/* Tabs */}
      <div className="border-b mb-6">
        <button className="px-4 py-2 border-b-2 border-green-600 font-medium text-gray-700">
          Profile
        </button>
      </div>

      {/* Profile Row */}
      <div className="bg-white shadow rounded-lg p-6 relative">
        <div className="flex flex-col md:flex-row items-start md:items-center gap-12">
          {/* Profile Image */}
          <div className="flex flex-col ml-10 items-center">
            <div className="w-28 h-28 rounded-full bg-gray-200 flex items-center justify-center text-4xl overflow-hidden">
              {profile.photo ? (
                <img
                  src={profile.photo}
                  alt="Profile"
                  className="w-full h-full object-cover"
                />
              ) : (
                "ðŸ‘¤"
              )}
            </div>

            {/* Hidden input */}
            <input
              type="file"
              id="profileUpload"
              className="hidden"
              accept="image/*"
              onChange={handleImageChange}
            />

            <button
              onClick={() => document.getElementById("profileUpload").click()}
              className="mt-2 text-sm border px-3 py-1 rounded bg-gray-50 hover:bg-gray-100"
            >
              Change
            </button>
          </div>

          {/* Name + Role */}
          <div className="flex-1 ml-20">
            <h2 className="text-xl font-semibold">{profile.name}</h2>
            <p className="text-gray-600">{profile.role}</p>
            <p className="text-green-600 text-sm mt-1">
              Last seen {profile.lastSeen}
            </p>

            {/* Bio */}
            <div className="mt-6">
              <p className="text-gray-500 text-sm font-medium">Bio</p>
              <p className="text-sm">{profile.bio || "None"}</p>
            </div>
          </div>

          {/* Details Section */}
          <div className="text-sm space-y-4 w-full md:w-1/3 md:ml-12">
            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Email</span>
              <span>
                {profile.email}{" "}
                {profile.emailVerified && (
                  <span className="text-green-600 ml-1">âœ”</span>
                )}
              </span>
            </div>

            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Mobile</span>
              <span className="text-gray-500">
                {profile.mobile || "No mobile number provided"}
              </span>
            </div>

            <div className="flex justify-between">
              <span className="font-medium text-gray-700">D.O.B.</span>
              <span>{profile.dob || "-"}</span>
            </div>

            <div className="flex flex-col">
              <h1 className="text-3xl font-bold text-gray-900">
                {userProfile?.firstName || user?.firstName || 'User'} {userProfile?.lastName || user?.lastName || ''}
              </h1>
              <p className="text-lg text-gray-600 mt-1">
                {userProfile?.jobTitle || 'No job title specified'}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                {userProfile?.company || 'No company specified'} â€¢ {userProfile?.staffType || 'Staff'} Staff
              </p>
            </div>

            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Preferred Language</span>
              <span>
                {profile.language ? (
                  profile.language
                ) : (
                  <span className="text-blue-600 cursor-pointer">
                    No language selected
                  </span>
                )}
              </span>
            </div>

            <div className="flex justify-between">
              <span className="font-medium text-gray-700">Address</span>
              <span>{profile.address || "-"}</span>
            </div>

            <div className="flex justify-between">
              <span className="font-medium text-gray-700">
                Other Information
              </span>
              <span>{profile.otherInfo || "None"}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
